import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as t,c,a as e,b as a,d as n,e as d}from"./app-eaa093f0.js";const s={},h={href:"http://hscarb.github.io/other/20230316-vim-linebreaker.html",target:"_blank",rel:"noopener noreferrer"},l=d('<h1 id="vim-显示、去除换行符" tabindex="-1"><a class="header-anchor" href="#vim-显示、去除换行符" aria-hidden="true">#</a> Vim 显示、去除换行符</h1><h2 id="背景" tabindex="-1"><a class="header-anchor" href="#背景" aria-hidden="true">#</a> 背景</h2><ul><li>Dos 和 windows：采用回车+换行（CR/LF）表示下一行</li><li>UNIX/Linux：采用换行符（LF）表示下一行</li><li>MAC OS：采用回车符（CR）表示下一行</li></ul><hr><ul><li>CR用符号<code>\\r</code>表示, 十进制ASCII代码是13, 十六进制代码为0x0D</li><li>LF用符号<code>\\n</code>表示, 十进制ASCII代码是10, 十六制为0x0A.</li></ul><h2 id="显示换行符" tabindex="-1"><a class="header-anchor" href="#显示换行符" aria-hidden="true">#</a> 显示换行符</h2><p><code>:set list</code> shows newline (<code>$</code>)</p><p><code>:e ++ff=unix</code> shows CR (<code>^M</code>)</p><p>if you want to see both, <code>:set list</code> then <code>:e ++ff=unix</code></p><h2 id="去除-cr" tabindex="-1"><a class="header-anchor" href="#去除-cr" aria-hidden="true">#</a> 去除 CR</h2><p><code>:%s/\\r//g</code></p><hr><p>欢迎关注公众号【消息中间件】（middleware-mq），更新消息中间件的源码解析和最新动态！</p><figure><img src="https://scarb-images.oss-cn-hangzhou.aliyuncs.com/img/202205170102971.jpg" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure>',14);function m(p,u){const i=o("ExternalLinkIcon");return t(),c("div",null,[e("p",null,[a("原文地址："),e("a",h,[a("http://hscarb.github.io/other/20230316-vim-linebreaker.html"),n(i)])]),l])}const b=r(s,[["render",m],["__file","20230316-vim-linebreaker.html.vue"]]);export{b as default};
